css:
@media screen, print{
.headingOfInv{
    display: flex;
    flex-direction: column;
    align-items: center;
}
/*.mainContainer{*/
/*    position: relative;*/
/*}*/
.container1{

    border:1px solid black;
    width:100%;
    height:100%;
    display:grid;
    grid-template-columns: repeat(13,1fr);
    grid-template-rows: repeat(9,1fr);
}

.box{
    border:1px solid black;
    padding:10px;
}

#box1{
    grid-column :1 / 10;
    grid-row: 1/3;
    word-wrap: break-word;
}

#box2{
    display: flex;
    flex-direction: column;
    align-items: center;
    grid-column:10 / 14;
    grid-row:1/5;
    word-wrap: break-word;
}
#box3{
    grid-column:1 / 5;
    grid-row:3/5;
    word-wrap: break-word;
}
#box4{
    grid-column:5 / 10;
    grid-row:3/5;
    word-wrap: break-word;
}
#billAddress{
    grid-column:1/6;
    grid-row:5/9;
    word-wrap: break-word;
    font-size:11px;
}
#deliveryAddress{
    grid-column:6 / 10;
    grid-row:5/9;
    word-wrap: break-word;
    font-size:11px;
}
#orderNo{
    grid-column:10 / 14;
    grid-row:5/9;
    word-wrap: break-word;
    font-size:11px;
}
