/*}*/

#billTableSr{
    grid-column:1/2;
}
#billTableMatCode{
    grid-column:2/4;
}
#billTableMatDesc{
    grid-column:4/8;
}
#billTableUOM{
    grid-column:8/9;
}
#billTableQty{
    grid-column:9/10;
}
#billTableRate{
    grid-column:10/11;
}
#billTableAmount{
    grid-column:11/14;
}

#TotalQtyForm{
    grid-column:1/7;
}
#TotalQtyForm1{
    grid-column:7/9;
}
#TotalQtyForm11{
    grid-column:7/11;
}
#TotalQtyForm2{
    grid-column:9/10;
}
#TotalQtyForm3{
    grid-column:10/11;
}
#TotalQtyForm4{
    grid-column:11/14;
}
.footer{
    /*position: absolute;*/

    border:1px solid black;
    width:100%;
    height:100%;
    display:grid;
    grid-template-columns: repeat(13,1fr);
    grid-template-rows: repeat(4,1fr);
}

/*#footerRemarks{*/
/*    grid-column:1/14;*/
/*    grid-row:1/3;*/
/*}*/
#footerNote{
    grid-column:1/9;
    grid-row:3/4;
    font-size:9px;
}

#footerAddress{
    grid-column:1/14;
    grid-row:4/5;
    font-size:10px;
    display:flex;
    justify-content:center;
}

#footerSignature{
    grid-column: 9/14;
    grid-row:1/4;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items:flex-end;
    font-size: 11px;
    font-weight: 700;
}


    .footer{
        page-break-after: always;
        page-break-before: always;
        bottom:0;
    }
}
